    <div className="sidebar">
      <div className="logo">
        <FontAwesomeIcon icon={faRocket} /> Provenance
      </div>
      <button className="new-question-btn" onClick={onNewQuestion}>
        <FontAwesomeIcon icon={faPlus} /> New question
        <span className="shortcut">âŒ˜ K</span>
      </button>
      <div className="sidebar-menu">
        <div className="menu-item active">
          <FontAwesomeIcon icon={faThLarge} /> My Space
          <span className="badge">1</span>
        </div>
        <div className="menu-item">
          <FontAwesomeIcon icon={faChartLine} /> Activity
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
