import React, { useEffect, useRef } from 'react';
import '../styles/ChatContainer.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFileAlt, faCommentAlt, faChevronDown, faTerminal, faSearch } from '@fortawesome/free-solid-svg-icons';

const ProcessLogs = ({ logs }) => {
  const logsEndRef = useRef(null);

  // Auto-scroll to bottom when new logs come in
  useEffect(() => {
    if (logsEndRef.current) {
      logsEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [logs]);

  if (!logs || logs.length === 0) return null;

  // Group logs by provenance entry
  const groupedLogs = [];
  let currentGroup = { type: 'standard', logs: [] };

  logs.forEach(log => {
    if (log.startsWith('Top-')) {
      // Start a new provenance group
      if (currentGroup.logs.length > 0) {
        groupedLogs.push(currentGroup);
      }
      currentGroup = { type: 'provenance', title: log, logs: [] };
    } else if (log.startsWith('Provenance:') || log.startsWith('Input tokens:') || 
              log.startsWith('Output tokens:') || log.startsWith('Time:')) {
      // Add to current provenance group
      if (currentGroup.type === 'provenance') {
        currentGroup.logs.push(log);
      } else {
        // Standard log
        currentGroup.logs.push(log);
      }
    } else {
      // Check if we need to finalize a provenance group
      if (currentGroup.type === 'provenance' && currentGroup.logs.length > 0) {
        groupedLogs.push(currentGroup);
        currentGroup = { type: 'standard', logs: [] };
      }

      // Regular log
      currentGroup.logs.push(log);
    }
  });

  // Add the last group
  if (currentGroup.logs.length > 0) {
    groupedLogs.push(currentGroup);
  }

  return (
    <div className="process-logs">
      <div className="logs-title">
        <FontAwesomeIcon icon={faTerminal} /> Processing Logs
      </div>
      <div className="logs-content">
        {groupedLogs.map((group, groupIndex) => (
          group.type === 'provenance' ? (
            <div key={groupIndex} className="provenance-log-group">
              <div className="provenance-title">
                <FontAwesomeIcon icon={faSearch} /> {group.title}
              </div>
              {group.logs.map((log, logIndex) => (
                <div key={logIndex} className={`log-entry ${log.startsWith('Provenance:') ? 'provenance-content' : ''}`}>
                  {log}
                </div>
              ))}
            </div>
          ) : (
            <div key={groupIndex} className="standard-log-group">
              {group.logs.map((log, logIndex) => (
                <div key={logIndex} className="log-entry">
                  {log}
                </div>
              ))}
            </div>
          )
        ))}
        <div ref={logsEndRef} />
      </div>
    </div>
  );
};

const ChatContainer = ({ currentQuestion, answer, isProcessing, uploadStatus, provenanceSources, logs }) => {
  return (
    <div className="chat-container">
      {currentQuestion && (
        <div className="question-display">
          <div className="card">
            <div className="card-title">
              <FontAwesomeIcon icon={faFileAlt} /> Question
            </div>
            <div className="card-content">{currentQuestion}</div>
          </div>

          <div className="card">
            <div className="card-title">
              <FontAwesomeIcon icon={faCommentAlt} /> Answer
            </div>
            <div className="card-content">{answer || 'Processing...'}</div>
          </div>
        </div>
      )}

      {logs && logs.length > 0 && (
        <ProcessLogs logs={logs} />
      )}

      {provenanceSources.length > 0 && (
        <div className="sources-container">
          <div className="sources-heading">Sources</div>
          <div className="sources-relevance">
            <span>Most relevant</span>
            <FontAwesomeIcon icon={faChevronDown} />
          </div>
          <div className="sources-list">
            {provenanceSources.map((source, index) => (
              <div key={index} className="source-card">
                <div className="source-number">
                  {index + 1}. Top-{source.provenance_id} Provenance
                </div>
                <div className="source-ids">
                  Sentence IDs: {source.sentences_ids ? source.sentences_ids.join(', ') : 'N/A'}
                </div>
                <div className="source-time">
                  Time: {source.time ? source.time.toFixed(2) : 'N/A'}s
                </div>
                <div className="source-tokens">
                  Tokens: Input: {source.input_token_size || 'N/A'}, Output: {source.output_token_size || 'N/A'}
                </div>
                <div className="source-content">
                  {source.content ? (
                    source.content.map((sentence, idx) => (
                      <div key={idx} className="sentence">
                        {sentence}
                      </div>
                    ))
                  ) : (
                    <div className="loading">Loading sentence content...</div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {uploadStatus && (
        <div className="upload-status">
          <div className={`file-status ${uploadStatus.success ? 'success' : 'error'}`}>
            {uploadStatus.message}
          </div>
        </div>
      )}

      {isProcessing && (
        <div className="processing">
          <div className="processing-text">Processing</div>
          <div className="dots">
            <span className="dot"></span>
            <span className="dot"></span>
            <span className="dot"></span>
          </div>
        </div>
      )}
    </div>
  );
};

export default ChatContainer;
